const hardSeniorPatterns = [
  /5\+\s*years/i,
  /6\+\s*years/i,
  /7\+\s*years/i,
  /8\+\s*years/i,
  /10\+\s*years/i,
  /senior/i,
  /staff/i,
  /principal/i,
  /architect/i,
  /\bSr\b\.?/i,
];

const borderlinePatterns = [
  /3-5\s*years/i,
  /4-6\s*years/i,
  /3\+\s*years/i,
];

return items.filter(item => {
  const title = item.json.job_title || "";
  const desc  = item.json.job_description || "";
  const text  = `${title} ${desc}`;

  // Drop obviously senior roles
  if (hardSeniorPatterns.some(rx => rx.test(text))) {
    return false;
  }

  // For now, also drop 3â€“5 / 3+ years as too strong
  if (borderlinePatterns.some(rx => rx.test(text))) {
    return false;
  }

  return true;
});

const out = [];
for (const item of items) {
  const data = item.json.data || [];
  for (const job of data) {
    out.push({ json: job });
  }
}
return out;
